import React, { Component, useState } from 'react';
import Head from 'next/head';
import {connect} from 'react-redux';
import { Modal, ModalHeader, ModalContent, ModalBody, ModalFooter, Button } from 'reactstrap';
import {toggleModal, hideModal, showModal} from '../redux/actions/modalActions';

import Header from '../components/Header';
import Footer from '../components/Footer';
import Player from '../components/Player';

class Layout extends Component {
	static getInitialProps({store}) {}

    constructor(props) {
        super(props);
	}
	
	render() {
		const { children, title } = this.props;

		return (
			<div className="App">
				<Head>
					<meta charSet="utf-8" />
					<meta name="viewport" content="initial-scale=1.0, width=device-width" />

					<title>{title}</title>

					<link rel="shortcut icon" href="favicon.ico" type="image/x-icon" />
				</Head>

				<Header />

				<main class="mt-5 container">
					{/* {children} */}
					{this.props.modal ? 'Mostrando' : 'Escondendo'}
					<br />
					<button onClick={this.props.showModal}>Show</button>
					<button onClick={this.props.hideModal}>Hide</button>
				</main>

				<Player />
				
				<Footer />


				<Modal isOpen={this.props.modal} toggle={this.props.toggleModal} size="lg">
					<div className="row no-gutters">
						<div className="col-md-6">
							<div className="bg-login">
								<h3>Um lugar divertido com gente incrível.</h3>
							</div>
						</div>
						
						<div className="col-md-6">
							<div className="toggle-login">
								<div className="login">
									<ModalHeader>
										<h3 className="modal-title" id="loginModalCenterTitle">Entrar</h3>
										<button type="button" className="close" data-dismiss="modal" aria-label="Close">
										<i className="fas fa-times" />
										</button>
									</ModalHeader>
									<ModalBody>
										<form name="loginform" id="loginform" action="https://marcocuel.com/p/fansite/wp-login.php" method="post">
										<p className="login-username">
											<label htmlFor="user_login">Nome de usuário ou endereço de e-mail</label>
											<input type="text" name="log" id="user_login" className="input" defaultValue size={20} />
										</p>
										<p className="login-password">
											<label htmlFor="user_pass">Senha</label>
											<input type="password" name="pwd" id="user_pass" className="input" defaultValue size={20} />
										</p>
										<p className="login-remember"><label><input name="rememberme" type="checkbox" id="rememberme" defaultValue="forever" /> Lembrar-me</label></p>
										<p className="login-submit">
											<input type="submit" name="wp-submit" id="wp-submit" className="button button-primary" defaultValue="Acessar" />
											<input type="hidden" name="redirect_to" defaultValue="https://marcocuel.com/p/fansite/entrar/" />
										</p>
										</form>
										<hr className="ou my-4" />
										<div className="text-center">
										<a href="https://marcocuel.com/p/fansite/registro" className="btn btn-lg btn-success show-register">Criar nova conta</a>
										</div>
									</ModalBody>
								</div>
							</div>
						</div>
					</div>
				</Modal>
			</div>
		);
	}
}

const mapStateToProps = state => ({
    modal: state.modal.value
});

const mapDispatchToProps = {
    showModal: showModal,
    hideModal: hideModal,
    toggleModal: toggleModal,
};

export default connect(mapStateToProps, mapDispatchToProps)(Layout);
