import React, { Component, useState } from 'react';
import Head from 'next/head';
import {connect} from 'react-redux';
import { Modal, ModalHeader, ModalBody, Button } from 'reactstrap';
import {hideModal, showModal} from '../redux/actions/modalActions';

import Header from '../components/Header';
import Footer from '../components/Footer';
import Player from '../components/Player';

class Layout extends Component {
	static getInitialProps({store}) {}

    constructor(props) {
        super(props);
	}
	
	render() {
		const { children, title } = this.props;

		return (
			<div className="App">
				<Head>
					<meta charSet="utf-8" />
					<meta name="viewport" content="initial-scale=1.0, width=device-width" />

					<title>{title}</title>

					<link rel="shortcut icon" href="favicon.ico" type="image/x-icon" />
				</Head>

				<Header />

				<main class="mt-5 container">
					{/* {children} */}
					{this.props.modal ? 'Mostrando' : 'Escondendo'}
					<br />
					<button onClick={this.props.showModal}>Show</button>
					<button onClick={this.props.hideModal}>Hide</button>
				</main>

				<Player />
				
				<Footer />


				<Modal isOpen={modal} toggle={toggle} className={className}>
					<ModalHeader toggle={toggle}>Modal title</ModalHeader>
					<ModalBody>
					Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
					</ModalBody>
					<ModalFooter>
					<Button color="primary" onClick={toggle}>Do Something</Button>{' '}
					<Button color="secondary" onClick={toggle}>Cancel</Button>
					</ModalFooter>
				</Modal>
			</div>
		);
	}
}

const mapStateToProps = state => ({
    modal: state.modal.value
});

const mapDispatchToProps = {
    showModal: showModal,
    hideModal: hideModal,
};

export default connect(mapStateToProps, mapDispatchToProps)(Layout);
